mapper.delete:
UPDATE MOZZLE_POST
	SET DELFLAG='Y'
	WHERE POST_ID= #{post_id}

mapper.select:
SELECT mp.*, m.MOZZLE_NAME, um.NICKNAME FROM MOZZLE_POST mp INNER JOIN MOZZLE m ON mp.MOZZLE_ID = m.MOZZLE_ID INNER JOIN USER_MOZZLE um ON um.USER_ID = mp.USER_ID WHERE mp.USER_ID = #{user_id}
AND mp.MOZZLE_ID = #{mozzle_id}
AND mp.DEPTH = '0' AND mp.DELFLAG = 'N' ORDER BY mp.REGDATE DESC
