mapper.update:
UPDATE ADMIN.MOZZLE
	SET MOZZLE_NAME=#{mozzle_name}, MOZZLE_INTRO=#{mozzle_intro}, 
	IMAGE_ORIGIN=#{image_origin}, IMAGE_SAVED=#{image_saved}, 
	CATEGORY_CODE=#{category_code}, STATE=#{state}
		WHERE MOZZLE_ID= #{mozzle_id}

mapper.select:
SELECT MOZZLE_ID 
FROM MOZZLE 
WHERE MOZZLE_NAME = #{mozzle_name}
